definition.script:
// Global Vars

["test"].each { environment ->
    node() {

        def ci_job_name = env."CI_JOB_JOBNAME"
        def ci_job_number = env."CI_JOB_NUMBER"
        def graph


        stage("[${environment.toUpperCase()}] Reading manifest") {
            artifact_path = "${env.'JOB_NAME'}_${env.'BUILD-NUMBER'}"
            echo "Saving graph from ${ci_job_name}:${ci_job_number} into ${artifact_path}"

            deleteDir()

            //Artifact
            copyArtifacts(projectName: ci_job_name, selector: specific(ci_job_number), target: artifact_path)
            def files = findFiles(glob: "${artifact_path}/*.yaml")
            echo "Artifact found: ${files[0]}"
            sh "cat ${files[0]}"

            //Read yaml
            graph = readYaml file: "${files[0]}"
        }


        //*************************************** KUBERNETES DEPLOYMENT ***************************************//

        if (graph.'kubernetes-config') {
            stage("[${environment.toUpperCase()}] Kubernetes Deployment") {
                def current_file_path
                def current_file

                dir(environment){

                    dir("kubernetes-config") {
                        deleteDir()

                        sh returnStatus: true, script: "kubectl create namespace ${environment}"
                        // In case you need to use credentials
                        git branch: "master", credentialsId: "6077620c-1a9f-45ee-9072-3fb08a0a62e7", url: graph.'kubernetes-config'.repo_url
                        //git branch: "main", url: graph.'kubernetes-config'.repo_url
                        sh script: "git checkout ${graph.'kubernetes-config'.revision}"


                        ["daemonset", "deployment"].each { resource_type ->
                            current_file_path = "kubernetes/${graph.service.name}/${environment}/${resource_type}.yaml"
                            echo "File: " + current_file_path

                            if (fileExists(current_file_path)) {

                                // Read and replace the bookmarks
                                current_file = readFile current_file_path
                                echo "Contend: " + current_file
                                current_file = current_file.replace("SERVICE_NAME", "${graph.app.project_branch}-${graph.service.name}")
                                echo "${graph.service.name}"
                                current_file = current_file.replace("SERVICE_TTL", "${graph.app.service_ttl}")
                                echo "${graph.app.service_ttl}"
                                current_file = current_file.replace("IMAGE_VERSION", "${graph.service.revision}")
                                echo "${graph.service.revision}"
                                current_file = current_file.replace("IMAGE", "${graph.app.project_branch}-${graph.service.name}")
                                echo "${graph.service.name}"

                                writeFile file: "resources_${environment}.yaml", text: current_file
                                echo current_file

                                sh script: "kubectl apply -n ${environment} -f resources_${environment}.yaml"

                                echo "File: resources_${environment}.yaml"
                                current_file = readFile "resources_${environment}.yaml"
                                echo "Content:" + current_file

                            }
                        }
                    }
                }
            }
        }


        //*************************************** TESTS ***************************************//
        if (graph.'kubernetes-config') {
            stage("[${environment.toUpperCase()}] Run Tests") {

                dir(environment){

                    dir("tests") {
                        deleteDir()

                        git branch: "master", credentialsId: "6077620c-1a9f-45ee-9072-3fb08a0a62e7", url: graph.'kubernetes-config'.repo_url
                        //git branch: "main", url: graph.'kubernetes-config'.repo_url

                        sh script: "git checkout ${graph.'kubernetes-config'.revision}"

                        ["daemonset", "deployment"].each { resource_type ->

                            if (fileExists("kubernetes/${graph.service.name}/${resource_type}.yaml")) {
                                if (resource_type == "deployment"){
                                    waitUntil() {
                                        sleep(5)
                                        rollout_status = sh returnStdout: true, script: "kubectl rollout status --watch=false -n ${environment} ${resource_type}/${graph.app.project_branch}_${graph.service.name}"
                                        rollout_status.contains("successfully rolled out")
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }

        def endpoint_status
        timeout(120) {
            waitUntil() {
                sleep(10)
                endpoint_status = sh returnStdout: true, script: "kubectl get endpoints -n test"
                return endpoint_status.contains("${graph.app.project_branch}-${graph.service.name}")
            }
        }

        echo "Endpoint status: $endpoint_status"


        def lines = endpoint_status.split('\n')
        def processName = "${graph.app.project_branch}"
        echo "Input parameter: $processName"
        def hasProcess  = { it.toLowerCase().contains(processName) }
        if(lines.any(hasProcess)) { 
          println "Sucess"
          def endPoint = lines.findAll(hasProcess).join('')
          def point = endPoint.replaceAll('   ', ' ').replaceAll('  ', ' ').split(' ')[1]
          echo "End point: $point"
          echo "==============================================================="
          echo "Send url to Jira"
          echo "Branch name: ${graph.app.branch_name}"
          echo "Service name: ${graph.app.project_branch}-${graph.service.name}"
          echo "Service version: ${graph.service.revision}"
          echo "Url link: http://$point/system/properties"
          echo "==============================================================="
        } else { 
          println "Failure"
        }

    }
}
